sys_script_include.script:
var triggerJobSplunkTOsRTMS = Class.create();
triggerJobSplunkTOsRTMS.prototype = {
    initialize: function() {
    },

	getTriggerJob: function(){
		var responseBody = "";
		try {
			//var vURI = '';

			var grsetting = new GlideRecord('x_85303_srtms_settings');

			grsetting.get('service','sRTMS');
			var vURI = grsetting.url;

			var vSplunkAPI = grsetting.splunk_api;
			var vSplunkLogin = grsetting.splunk_login;
			var vSplunkPassword = grsetting.splunk_password.getDecryptedValue();
			var vSessionToken = grsetting.sessiontoken;
			var vJobName = grsetting.job_name;
			var vLastRun = grsetting.last_run;

			var vDeviceSearch = grsetting.device_search;
			var vPackageSearch = grsetting.package_search;

			var r = new sn_ws.RESTMessageV2('sRTMS - Splunk - JobTrigger', 'Default POST');

			r.setStringParameter('URI', vURI);
			r.setStringParameter('SPLUNKAPI', vSplunkAPI);
			r.setStringParameter('SPLUNKLOGIN', vSplunkLogin);
			r.setStringParameter('SPLUNKPASSWORD', vSplunkPassword);
			r.setStringParameterNoEscape('DEVICESEARCH', vDeviceSearch);
			r.setStringParameterNoEscape('PACKAGESEARCH', vPackageSearch);

			r.setStringParameter('SESSIONTOKEN', vSessionToken);
			r.setStringParameter('JOBNAME', vJobName);

			var response = r.execute();
			responseBody = response.getBody();
			var httpStatus = response.getStatusCode();

			var js = JSON.parse(responseBody);
			var error_code = js.error_code;
			var success = js.success;
			var callLog = new logTOsRTMS();

			if(httpStatus=="200" && error_code=="0" && success==true)
			{	
				var jobID = js.results.jobId;
				grsetting.splunk_job_id = jobID;
				grsetting.update();


				callLog.setLog(vLastRun,'Info','Trigger','Splunk JobID: '+jobID);

				//responseBody="1";
				responseBody="1";
			}
			else
			{
					callLog.setLog(vLastRun,'Info','Trigger','Error: '+responseBody);
			}
		}
		catch(ex) {
			var message = ex.getMessage();
			responseBody = ex.getMessage();
		}

		return responseBody;
	},

    type: 'triggerJobSplunkTOsRTMS'
};
